/* Import dependencies */
import com.mongodb.spark._
import com.mongodb.spark.config._
import org.bson.Document

/* Create a sequence of json-formatted strings */
val docs = """
  {"name": "Bilbo Baggins", "age": 50}
  {"name": "Gandalf", "age": 1000}
  {"name": "Thorin", "age": 195}
  {"name": "Balin", "age": 178}
  {"name": "Kíli", "age": 77}
  {"name": "Dwalin", "age": 169}
  {"name": "Óin", "age": 167}
  {"name": "Glóin", "age": 158}
  {"name": "Fíli", "age": 82}
  {"name": "Bombur"}""".trim.stripMargin.split("[\\r\\n]+").toSeq

/* parse the json-formatted strings as MongoDB document */
val documents = sc.parallelize(docs.map(Document.parse))

/**
 * NOTE TO USER: In your terminal, run "docker ps" to see the names of your currently running docker containers.
 * Find the name of the MongoDB container and paste it into the string below.
 */
val mongoDbContainerName = "airbnbbot_mongodb_1" // <- PASTE YOUR MONGODB CONTAINER NAME HERE
val dbName = "myDB"
val collectionName = "myCollection"
val mongoDbUri = "mongodb://" + mongoDbContainerName + "/" + dbName + "." + collectionName

/* Save documents to MongoDB (the one in the docker container) */
println("Saving data to " + mongoDbUri)
documents.saveToMongoDB(WriteConfig(Map("uri" -> mongoDbUri)))
println("Done.")
